mapper.select:
select member_seq, member_id, member_nick 
from members 
where member_id = #{memberId} and member_pass = #{memberPass}

mapper.update:
update members 
set member_nick = #{memberNick}, member_pass = #{memberPass}, member_img_path = #{memberImgPath}
, member_img_origin_name = #{memberImgOriginName}, member_img_server_name = #{memberImgServerName}
where member_seq = #{memberSeq}

mapper.select:
select member_name, member_id, member_email, member_nick
, member_img_path, member_img_origin_name, member_img_server_name 
where member_seq = #{memberSeq}
