mapper.select:
select m.member_seq, member_nick, member_id, member_img_path , member_img_origin_name, member_img_server_name from members m join friend_request f
on m.member_seq = f.member_seq
on m.member_seq = f.friend_request_member
where friend_request_is_agree = 0
and f.friend_request_member = #{memberSeq}
and f.member_seq = #{memberSeq}

mapper.update:
update friend_request 
set freind_request_is_agree = 1 
where friend_request_seq = #{friendRequestSeq}

mapper.select:
select member_nick, member_id, member_img_path, member_img_origin_name, member_img_server_name 
from members 
where member_id = #{memberInfo} or member_phone_num = #{memberInfo}
