mapper.update:
update promises
promise_title = #{promiseTitle}, promise_leader = #{promiseLeader}, promise_date = #{promiseDate}, promise_time = #{promiseTime}, promise_day = #{promiseDay}, promise_vote_is_finish = #{promiseVoteIsFinish}, promise_schedule_is_finish = #{promiseScheduleIsFinish},
where promise_seq = #{promiseSeq}
